["event"] = "Conditions",
["custom"] = "function(event)\n    local timeSt,message,_,sourceGUID,_,_,_,destGUID,_,_,_,spellID,spellName,_,spellDamage,_,_,_,_,_,crit = CombatLogGetCurrentEventInfo()\n    \n    aura_env.valkyrOn = aura_env.valkyrOn or false \n    aura_env.valkyrId = aura_env.valkyrId or (\" \")\n    aura_env.valkyrPwr = aura_env.valkyrPwr or 0\n    aura_env.totalDamage = aura_env.totalDamage or 0\n    aura_env.castsNumber = aura_env.castsNumber or 0\n    aura_env.summonTime = aura_env.summonTime or 0\n    aura_env.valCritNum = aura_env.valCritNum or 0\n    aura_env.maxCrit=aura_env.maxCrit or 0\n    aura_env.haveTotem = GetTotemInfo(3)\n    aura_env.firstHit = aura_env.firstHit or false\n    aura_env.castTime = aura_env.castTime or 0\n    \n    --Banter's Variables\n    aura_env.raw_valkyrPwr = aura_env.raw_valkyrPwr or 0 \n    aura_env.shoulderOn = aura_env.shoulderOn or false\n    aura_env.doubleCastCheck = aura_env.doubleCastCheck or false\n    \n    \n    \n    -- Check DA Summon\n    \n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" and message == \"SPELL_SUMMON\" and sourceGUID == UnitGUID(\"player\") and spellID == 49206 then \n        aura_env.firstHit = false\n        aura_env.summonTime = GetTime()\n        aura_env.valkyrOn = true\n        aura_env.valkyrId = destGUID\n        aura_env.valkyrPwr=0\n        aura_env.valCritNum = 0\n        aura_env.castsNumber=0\n        aura_env.totalDamage=0\n        aura_env.maxCrit = 0\n        \n        --Banter's Variables\n        aura_env.shoulderOn = false\n        aura_env.raw_valkyrPwr = 0\n        aura_env.doubleCastCheck = false\n    end\n    \n    --Check if Shadow empowered removed\n    \n    if (event == \"COMBAT_LOG_EVENT_UNFILTERED\" and message == \"SPELL_AURA_REMOVED\" and sourceGUID == UnitGUID(\"player\") and spellID == 211947) then \n        aura_env.valkyrOn = false\n        aura_env.firstHit = false\n        aura_env.doubleCastCheck = false\n        \n    end  \n    \n    -- Apply Taktheretrix bool\n    if aura_env.valkyrOn == true then\n        if aura_env.shoulderOn == false then\n            \n            aura_env.valkyrPwr = aura_env.raw_valkyrPwr\n            \n            if (event == \"COMBAT_LOG_EVENT_UNFILTERED\" and message == \"SPELL_AURA_APPLIED\" and sourceGUID == UnitGUID(\"player\") and spellID == 309264) then \n                aura_env.shoulderOn = true \n                aura_env.valkyrPwr = aura_env.raw_valkyrPwr * 1.35 + 35\n            end\n        end\n    end\n    \n    --Death Coil Cast check, Thanks T21\n    if (event == \"COMBAT_LOG_EVENT_UNFILTERED\" and message == \"SPELL_CAST_SUCCESS\" and sourceGUID == UnitGUID(\"player\") and spellID == 47541) then \n        \n        aura_env.doubleCastCheck = true\n    end\n    \n    \n    -- Death Coil damage    \n    if aura_env.valkyrOn == true then\n        if event == \"COMBAT_LOG_EVENT_UNFILTERED\" and message == \"SPELL_DAMAGE\" and sourceGUID == UnitGUID(\"player\") and spellID == 47632 then \n            \n            if aura_env.shoulderOn == true then\n                if aura_env.doubleCastCheck == true then\n                    aura_env.doubleCastCheck = false\n                    aura_env.valkyrPwr = aura_env.valkyrPwr + 28\n                end\n            else\n                if aura_env.doubleCastCheck == true then\n                    aura_env.doubleCastCheck = false\n                    aura_env.raw_valkyrPwr = aura_env.raw_valkyrPwr + 30 --Death coil = 40RP\n                end\n            end\n        end\n    end\n    \n    \n    -- Death Strike damage     \n    if aura_env.valkyrOn == true then\n        if event == \"COMBAT_LOG_EVENT_UNFILTERED\" and message == \"SPELL_DAMAGE\" and sourceGUID == UnitGUID(\"player\") and spellID == 49998 then \n            if aura_env.shoulderOn == true then\n                aura_env.valkyrPwr = aura_env.valkyrPwr + 31.5\n            else\n                aura_env.raw_valkyrPwr = aura_env.raw_valkyrPwr + 35 -- Death Strike = 45RP\n            end\n        end\n    end\n    \n    -- Epidemic damage     \n    if aura_env.valkyrOn == true then\n        if event == \"COMBAT_LOG_EVENT_UNFILTERED\" and message == \"SPELL_CAST_SUCCESS\" and sourceGUID == UnitGUID(\"player\") and spellID == 207317 then \n            if aura_env.shoulderOn == true then\n                aura_env.valkyrPwr = aura_env.valkyrPwr + 21\n            else\n                aura_env.raw_valkyrPwr = aura_env.raw_valkyrPwr + 30 -- Epidemic = 30RP\n            end\n        end\n    end\n    \n    \n    --valkyr damage\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" and message == \"SPELL_DAMAGE\" and sourceGUID == aura_env.valkyrId then \n        \n        if aura_env.firstHit==false then\n            aura_env.castTime=GetTime()\n            aura_env.firstHit=true\n        end\n        \n        aura_env.totalDamage=aura_env.totalDamage+spellDamage\n        --crit check\n        if crit == false then\n            aura_env.castsNumber=aura_env.castsNumber+1\n        else\n            aura_env.valCritNum=aura_env.valCritNum+1\n            if spellDamage>aura_env.maxCrit then\n                aura_env.maxCrit=spellDamage\n            end            \n        end\n    end \n    \n    \n    aura_env.timeTrig=GetTime()-aura_env.summonTime\n    if aura_env.timeTrig>0 and aura_env.timeTrig<=38 then\n        return true\n    end\nend",
